mapper.select:
SELECT
    *
FROM
    t_stock_price_week
WHERE
    fq_type = #{fqType}
    AND
    stock_id = #{stockId}
    AND
    dt >= #{startDate}
    AND
    dt <= #{endDate}
ORDER BY
    dt ASC;

SELECT
    *
FROM
    t_stock_price_week
WHERE
    fq_type = #{fqType}
    AND
    stock_id = #{stockId}
    AND
    dt = #{dt}
LIMIT 1;
